package com.example.administrator.bazipaipan.db;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteOpenHelper;import android.database.sqlite.SQLiteDatabase.CursorFactory;public class SDDBOpenHelper extends SQLiteOpenHelper {	private final static int VERSION = 1;	public SDDBOpenHelper(Context context, String name, CursorFactory factory,			int version) {		super(context, name, factory, version);	}	public SDDBOpenHelper(Context context, String name, CursorFactory factory) {		this(context, name, null, VERSION);	}	public SDDBOpenHelper(Context context, String name) {		this(context, name, null);	}	public SDDBOpenHelper(Context context) {		this(context, "china_province_city_zone.db", null);	}	@Override	public void onCreate(SQLiteDatabase db) {	}	@Override	public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {		onCreate(db);	}}